 bool ATimerService::isTimeGreater(etTime* t1, etTime* t2) {
	if (t1->sec > t2->sec)
		return ET_TRUE;
	if (t1->sec < t2->sec)
		return ET_FALSE;
	if (t1->nSec > t2->nSec)
		return ET_TRUE;
	return ET_FALSE;
}
 void ATimerService::addTime(etTime* t1, etTime* t2) {
	t1->sec += t2->sec;
	t1->nSec += t2->nSec;
	while (t1->nSec >= 1000000000L) {
		t1->sec++;
		t1->nSec -= 1000000000L;
	}
}

